(A:ATC TYPE, string) uc 'CESSNA' scmp 0 == if{ 62 }
(A:ATC TYPE, string) uc 'BEECH' scmp 0 == if{ 92 }
(A:ATC TYPE, string) uc 'MOONEY' scmp 0 == if{ 85 }

(A:ATC TYPE, string) uc 'CESSNA' scmp 0 == if{ 74 }
(A:ATC TYPE, string) uc 'BEECH' scmp 0 == if{ 105 }
(A:ATC TYPE, string) uc 'MOONEY' scmp 0 == if{ 105 }

(@g:timerDirection) ! (>@g:timerDirection) 
(* state = running *)
(@g:timerRunState) 1 == 
if{
	(* just switched to countdown mode *)
	(@g:timerDirection) 1 == 
	if{
		(E:Zulu time, seconds) (@g:timerStartTime) - (>@g:timerOffset)
		(@g:timerOffset) (>@g:timerCountdownReset)
	}
	els{
		(@g:timerOffset) (E:Zulu time, seconds) (@g:timerStartTime) - - (>@g:timerOffset)
	}
	(* if we're running *)
	(E:Zulu time, seconds) (>@g:timerStartTime)
}
(* in stopped state *)
(@g:timerRunState) 2 ==
if{
	(@g:timerDirection) 1 == 
	if{
		(@g:timerOffset) (@g:timerStopTime) (@g:timerStartTime) - 2 * + (>@g:timerOffset)
	}
	els{
		(@g:timerOffset) (@g:timerStopTime) (@g:timerStartTime) - 2 * - (>@g:timerOffset) 
	}
}

(* start the timer running *)
(@g:timerRunState) 0 ==
if{ 
	1 (>@g:timerRunState)
	(E:Zulu Time, seconds) (>@g:timerStartTime)
	(@g:timerDirection) 0 == 
	if{
		0 (>@g:timerOffset)
	}
	els{
		0 (@g:timerOffset) == 
		if{
			0 (>@g:timerDirection)
		}
	}
	quit
}
(* Stop the timer *)
(@g:timerRunState) 1 == 
if{
	2 (>@g:timerRunState)
	(E:Zulu Time, seconds) (>@g:timerStopTime)
	quit
}
(* Reset timer *)	
(@g:timerRunState) 2 == 
if{		
	0 (>@g:timerRunState)
	(@g:timerDirection) 0 == 
	if{
		(@g:timerStopTime) (>@g:timerStartTime)
	}
	els{
		(@g:timerCountdownReset) (>@g:timerOffset)
	}
	quit
}

